from django.conf.urls import patterns, include, urlfrom django.conf import settingsfrom django.contrib.auth.forms import SetPasswordForm    urlpatterns = patterns('',    url(r'^auths/register/$', 'auths.views.register'),    url(r'^auths/login/$', 'auths.views.login_user'),    url(r'^auths/logout/$', 'django.contrib.auth.views.logout', {'template_name': 'auths/logout.html'}),    url(r'^auths/pwd_change/$', 'django.contrib.auth.views.password_change', {'template_name': 'auths/pwd_change.html', 'post_change_redirect': '/',                                                                                            'password_change_form': SetPasswordForm}),)