from django import templateregister = template.Library()@register.filterdef getFavicon(url):    suffix = ['ac', 'cn', 'edu', 'gov', 'org', 'mil', 'arpa', 'net', 'biz', 'info', 'pro', 'name', 'coop',              'cc', 'tv', 'int', 'us', 'travel', 'idv', 'aero', 'ca', 'jp', 'kr', 'tw', 'com']    for one in suffix:        s = '.'+ one        if s in url:            return url[:url.find(s)+len(s)] + '/favicon.ico'    return ''@register.filterdef filterDeletedLinks(linklist):    '''        pick out links with deleted tag setted    '''    return (link for link in linklist if not link.deleted)@register.filterdef fillTableTd(num, column):    '''        fill the table's td left which will format the table view    '''    return range(int(column)-(int(num))%(int(column)))