from django import templateregister = template.Library()@register.filterdef getFavicon(url):    suffix = ['ac', 'com', 'edu', 'cn', 'gov', 'org', 'mil', 'arpa', 'net', 'biz', 'info', 'pro', 'name', 'coop',              'cc', 'tv', 'int', 'us', 'travel', 'idv', 'aero', 'ca', 'jp', 'kr', 'tw']    for one in suffix:        s = '.'+ one        if s in url:            return url[:url.find(s)+len(s)] + '/favicon.ico'    return ''